import { Component, ViewChild } from '@angular/core';
import { NavbarComponent } from './components/navbar/navbar.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  @ViewChild('drawer') drawer: any;

  constructor() {}

  closeDropdown() {
    // Chiudi il dropdown
    this.toggleNavbar();
    console.log('ciao');
  }

  private toggleNavbar() {
    const navbarToggler = document.querySelector('.navbar-toggler') as HTMLElement;
    navbarToggler.click();
  }

  title = 'bologna';
}

<app-navbar></app-navbar>
<app-footer></app-footer>
<mat-drawer-container>
    <mat-drawer #drawer>
        <div class="container-navbar">
            <p class="user">Ciao User</p>
            <a class="profile" routerLink="/profile" routerLinkActive="active">Il mio profilo</a>
          <hr>
            <ul class="navbar-nav">


                <a class="nav-link" routerLink="/centri" routerLinkActive="active">I CENTRI BG</a>

              <hr>

              <a class="nav-link" routerLink="/preventivo" routerLinkActive="active">PREVENTIVO</a>

              <hr>


              <a class="nav-link" routerLink="/prenotazione" routerLinkActive="active">PRENOTAZIONE</a>

              <hr>
              <a class="nav-link" routerLink="/veicoli" routerLinkActive="active">I MIEI VEICOLI</a>

              <hr>

              <a class="nav-link" routerLink="/bonus" routerLinkActive="active">BONUS BG</a>

              <hr>
              <a class="nav-link" routerLink="/ricevute" routerLinkActive="active">PAGAMENTI RICEVUTE</a>

              <hr>
              <a class="nav-link" routerLink="/blog" routerLinkActive="active">IL BLOG BG</a>

              <hr>
              <a class="nav-link" routerLink="/blog" routerLinkActive="active">LAVORA CON NOI</a>

              <hr>
              <a class="nav-link" routerLink="/blog" routerLinkActive="active">LA TUA OPINIONE</a>

              <hr>

                <a class="nav-link active" aria-current="page" routerLink="/home" routerLinkActive="active"
                  [routerLinkActiveOptions]="{ exact: true }">HOME</a>

              <hr>

                <a class="nav-link" routerLink="/login" routerLinkActive="active">LOGIN</a>

              <hr>
              <button>ACCADEMIA FELICITÀ</button>
            </ul>



          </div>
    </mat-drawer>
    <router-outlet></router-outlet>
  </mat-drawer-container>
